import React, { useState, useEffect } from 'react';
import { auth } from "../../Components/firebase/firebase"; // Assuming you have a firebase config file
import { useNavigate } from "react-router-dom";
import { getAuth, currentUser } from "firebase/auth"; // Assuming you have imported auth
import { getDatabase, ref, onValue } from "firebase/database";

function UpdateUser({ user }) {
  const [userInfo, setUserInfo] = useState({
    email: '',
    name: '',
    // Thêm các trường khác mà bạn muốn người dùng cập nhật
  });
  const [isLoading, setIsLoading] = useState(false); // For loading state
  const [error, setError] = useState(null); // For error messages

  useEffect(() => {
    const userInfo = getUserInfo(user); // Pass user to getUserInfo
    setUserInfo(userInfo);
  }, [user]); // Rerun effect when user changes

  const handleChange = (event) => {
    setUserInfo({
      ...userInfo,
      [event.target.name]: event.target.value,
    });
  };

  function getUserInfo(user) {
    const db = getDatabase();
    const userRef = ref(db, 'users/' + user.uid);

    onValue(userRef, (snapshot) => {
        const data = snapshot.val();
        if (data) {
            // User data exists, return their info
            return {
                displayName: data.username,
                email: data.email,
                // Add any other info you want to retrieve
            };
        } else {
            // No user data exists
            return null;
        }
    });
}

const updateUserInfo = async (updatedInfo) => {
    const user = auth.currentUser;
  
    if (user) {
      try {
        // Cập nhật thông tin hiển thị của người dùng
        await user.updateProfile({
          displayName: updatedInfo.displayName,
          // Thêm các trường thông tin khác bạn muốn cập nhật
        });
  
        // Cập nhật email nếu có thay đổi
        if (updatedInfo.email !== user.email) {
          await user.updateEmail(updatedInfo.email);
        }
  
        // Cập nhật mật khẩu nếu có thay đổi
        if (updatedInfo.password) {
          await user.updatePassword(updatedInfo.password);
        }
  
        console.log("Thông tin người dùng đã được cập nhật thành công.");
      } catch (error) {
        console.error("Có lỗi xảy ra khi cập nhật thông tin người dùng:", error);
      }
    } else {
      console.error("Không có người dùng nào đăng nhập.");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    setError(null); // Clear previous errors

    try {
      // Giả sử bạn có một hàm updateUserInfo() để cập nhật thông tin người dùng
      await updateUserInfo(userInfo);
      alert('User information updated successfully!'); // Consider using a success toast
      // Redirect to profile page or another relevant location
    } catch (error) {
      setError(error.message); // Display error message
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p className="error">{error}</p>}
      <label>
        Email:
        <input type="email" name="email" value={userInfo.email} onChange={handleChange} />
      </label>
      <label>
        Name:
        <input type="text" name="name" value={userInfo.name} onChange={handleChange} />
      </label>
      {/* Thêm các input khác mà bạn muốn người dùng cập nhật */}
      {isLoading ? (
        <button type="button" disabled>Updating...</button>
      ) : (
        <button type="submit">Update</button>
      )}
    </form>
  );
}

export default UpdateUser;
