import React, { useState, useEffect } from 'react';
import { auth } from "../../Components/firebase/firebase"; // Assuming you have a firebase config file
import { useNavigate } from "react-router-dom";
import { getAuth, currentUser } from "firebase/auth"; // Assuming you have imported auth
import { getDatabase, ref, onValue, update } from "firebase/database";

function Update() {
    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [userId, setUserId] = useState(""); // Add this line

    useEffect(() => {
        const user = auth.currentUser;
        if (user) {
            setUserId(user.uid);
        }
    }, []);

      const handleSubmit = (event) => {
        event.preventDefault();
        const updates = {};
        if (email) {
          updates.email = email;
        }
        if (username) {
          updates.username = username;
        }
      
        if (Object.keys(updates).length > 0) { // Check if any updates are needed
          update(ref(getDatabase(), 'users/' + userId), updates)
            .then(() => {
              alert('Cập nhật thành công !!!');
            })
            .catch((error) => {
              alert('Lỗi');
            });
        } else {
          alert('No changes to update');
        }
    }
    
    return (
        <form onSubmit={handleSubmit}> {/* Update this line */}
                <input
                id="username"
                  type="username"
                  autoComplete="off"
                  required
                  value={username}
                  placeholder ="Input your username"
                  onChange={(e) => {
                    setUsername(e.target.value);
                  }}
                  className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:indigo-600 shadow-sm rounded-lg transition duration-300"
                />
                <input
                id="email"
                  type="email"
                  autoComplete="off"
                  required
                  value={email}
                  placeholder ="Input your email"
                  onChange={(e) => {
                    setEmail(e.target.value);
                  }}
                  className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:indigo-600 shadow-sm rounded-lg transition duration-300"
                />
        <button>Update</button>
      </form>
    )
}

export default Update;
