import React from 'react';
import SignIn from './Components/googleSignIn/signIn'; // Replace with your actual login component path
import './App.css'; // Import your CSS styles (optional)
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./Components/googleSignIn/Home";
import { Toaster } from 'react-hot-toast';

// Tạo một thanh tiến trình tùy chỉnh
const ProgressBar = ({ duration }) => {
  const [time, setTime] = React.useState(duration);

  React.useEffect(() => {
    const timer = setInterval(() => {
      setTime((prevTime) => prevTime - 100);
    }, 100);

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div
      style={{
        height: '4px',
        width: `${(time / duration) * 100}%`,
        backgroundColor: 'green',
        transition: 'width 0.1s ease-in-out',
        position: 'absolute',
        bottom: 0,
      }}
    />
  );
};

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<SignIn />} />
          <Route path="/home" element={<Home />} />
        </Routes>
      </div>
      <Toaster
        position="top-right"
        reverseOrder={false}
        toastOptions={{
          // Tùy chỉnh thanh tiến trình cho mỗi toast
          success: {
            duration: 5000,
            style: {
              border: '1px solid green',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          },
          error: {
            duration: 5000,
            style: {
              border: '1px solid red',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          },
        }}
      />
    </Router>
  );
}


export default App;
