import React, { useState, useEffect } from 'react';
import { auth } from "../../Components/firebase/firebase"; // Assuming you have a firebase config file
import { useNavigate } from "react-router-dom";
import { getAuth, currentUser } from "firebase/auth"; // Assuming you have imported auth
import { getDatabase, ref, onValue, update } from "firebase/database";

function Update() {
    function updateDataBase(userId, email, name) {
        const db = getDatabase();
        update(ref(db, 'users/' + userId), {
          email: email,
          username: name,
        }, function (error) {
          if (error) {
            alert('Lỗi');
          } else {
            alert('Cập nhật thành công !!!');
          }
        });
    }
}

export default Update
