import React from 'react';
import SignIn from './Components/googleSignIn/signIn'; // Replace with your actual login component path
import './App.css'; // Import your CSS styles (optional)
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./Components/googleSignIn/Home";
import toast, { Toaster } from 'react-hot-toast';
import 'react-hot-toast/dist/index.css';

const ProgressBar = ({ hasFinished, delay }) => {
  const [progress, setProgress] = React.useState(0);

  React.useEffect(() => {
    if (hasFinished) {
      setProgress(100);
    } else {
      const interval = setInterval(() => {
        setProgress((oldProgress) => {
          if (oldProgress === 100) {
            clearInterval(interval);
            return 100;
          }
          const newProgress = oldProgress + 100 / (delay / 100);
          return Math.min(newProgress, 100);
        });
      }, 100);
      return () => {
        clearInterval(interval);
      };
    }
  }, [delay, hasFinished]);

  return (
    <div
      style={{
        height: '4px',
        width: `${100 - progress}%`,
        backgroundColor: 'green',
        transition: 'width 0.1s ease-in-out',
        position: 'absolute',
        bottom: 0,
      }}
    />
  );
};

function App() {
  return(
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<SignIn />} />
          <Route path="/home" element={<Home />} />
        </Routes>
      </div>
      <Toaster
        position="top-right"
        reverseOrder={false}
        toastOptions={{
          // Tùy chỉnh thanh tiến trình cho mỗi toast
          success: {
            duration: 5000,
            style: {
              border: '1px solid green',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          },
          error: {
            duration: 5000,
            style: {
              border: '1px solid red',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          },
        }}
      />
    </Router>
  );
}

export default App;
