import React, { useState, useEffect } from 'react';
import { auth } from "../../Components/firebase/firebase"; // Assuming you have a firebase config file
import { useNavigate } from "react-router-dom";
import { getAuth, currentUser } from "firebase/auth"; // Assuming you have imported auth
import { getDatabase, ref, onValue, update } from "firebase/database";

function Update() {
    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [userId, setUserId] = useState(""); // Add this line

    function updateDataBase(userId, email, name) {
        const db = getDatabase();
        update(ref(db, 'users/' + userId), {
          email: email,
          username: name,
        }, function (error) {
          if (error) {
            alert('Lỗi');
          } else {
            alert('Cập nhật thành công !!!');
          }
        });
    }

    const handleChange = (event) => {
        const { name, value } = event.target;
        if (name === "email") {
          setEmail(value);
        }  else if (name === "username") {
          setUsername(value);
        } 
      };

    const handleSubmit = (event) => {
        event.preventDefault();
        updateDataBase(userId, email, username); // Update this line
    }
    
    return (
        <form onSubmit={handleSubmit}> {/* Update this line */}
                <input
                id="username"
                  type="username"
                  autoComplete="off"
                  required
                  value={username}
                  placeholder ="Input your username"
                  onChange={(e) => {
                    setUsername(e.target.value);
                  }}
                  className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:indigo-600 shadow-sm rounded-lg transition duration-300"
                />
                <input
                id="email"
                  type="email"
                  autoComplete="off"
                  required
                  value={email}
                  placeholder ="Input your email"
                  onChange={(e) => {
                    setEmail(e.target.value);
                  }}
                  className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:indigo-600 shadow-sm rounded-lg transition duration-300"
                />
        <button>Update</button>
      </form>
    )
}

export default Update;
