import React from 'react';
import SignIn from './Components/googleSignIn/signIn'; // Replace with your actual login component path
import './App.css'; // Import your CSS styles (optional)
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./Components/googleSignIn/Home";
import toast, { Toaster } from 'react-hot-toast';

const ProgressBar = ({ percentage, value }) => (
  <div
    style={{
      height: '4px',
      width: `${value / percentage * 100}%`,
      backgroundColor: 'green',
      transition: 'width 0.3s ease-in-out',
    }}
  />
);

function App() {
  return (
    <Router>
        <div className="App">
      <Routes>
      <Route path="/" element={<SignIn />} />
      <Route path="/home" element={<Home />} />
      </Routes>
    </div>
    <Toaster
        position="top-right"
        reverseOrder={false}
        toastOptions={{
          // Tùy chỉnh thanh tiến trình cho mỗi toast
          success: {
            duration: 5000,
            style: {
              border: '1px solid green',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          },
          error: {
            duration: 5000,
            style: {
              border: '1px solid red',
            },
            toastBar: ProgressBar, // Sử dụng thanh tiến trình tùy chỉnh
          }
        }}
        />
      </Router>
  );
}

export default App;
