import React from 'react';
import SignIn from './Components/googleSignIn/signIn'; // Replace with your actual login component path
import './App.css'; // Import your CSS styles (optional)
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./Components/googleSignIn/Home";
import { Toaster } from 'react-hot-toast';

// Tạo một thanh tiến trình tùy chỉnh
const CustomToast = ({ message, type, ...props }) => {
  const toastClass = type === 'success' ? 'success-toast' : 'error-toast'; // Adjust class names

  return (
    <div className={`toast ${toastClass}`} {...props}>
      {/* Your toast content here */}
      <span className="message">{message}</span>
      <span className="countdown"></span>
    </div>
  );
};


function App() {
   return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<SignIn />} />
          <Route path="/home" element={<Home />} />
        </Routes>
      </div>
      <Toaster
        position="top-right"
        reverseOrder={false}
        toast={(message, type) => <CustomToast message={message} type={type} />} // Use custom toast component
      />
    </Router>
  );
}


export default App;
