import React, { useState, useEffect } from "react";
import { auth, provider } from "./config"; // Assuming config file with Firebase settings
import {
  signInWithPopup,
  signInWithEmailAndPassword,
} from "firebase/auth";
import Home from "./Home";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userEmail, setUserEmail] = useState(null); // Track logged-in user email
  const [error, setError] = useState(null); // Store error messages
  const [isSigningIn, setIsSigningIn] = useState(false); // Track sign-in state


  // Handle login with email and password
  const handleEmailLogin = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    setError(null); // Clear any previous errors

    setIsSigningIn(true); // Set signing in state

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCredential.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
      console.log('Signed in with email and password:', userCredential);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsSigningIn(false); // Reset signing in state
    }
  };

  // Handle Google sign-in
  const handleGoogleLogin = async () => {
    try {
      const data = await signInWithPopup(auth, provider); // Replace 'provider' with your Google Sign-in provider
      const userEmail = data.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
      console.log('Signed in with Google:', data);
    } catch (error) {
      setError(error.message);
    }
  };


  useEffect(() => {
    const storedEmail = localStorage.getItem("email");
    setUserEmail(storedEmail);
  }, []); // Empty dependency array ensures it runs only once

  return (
    <div className="SignIn">
      {error && <p className="error">{error}</p>} {/* Display error message if any */}
      {!userEmail && ( // Only show login options if not logged in
        <>
          <h2>Login</h2>
          
          <form onSubmit={handleEmailLogin}>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
              required
            />
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
              required
            />
            <button type="submit" disabled={isSigningIn}>
              {isSigningIn ? "Signing In..." : "Login with Email"}
            </button>
          </form>
          <button onClick={handleGoogleLogin} disabled={isSigningIn}>
            {isSigningIn ? "Signing In..." : "Login with Google"}
          </button>
          
        </>
      )}
       {userEmail && <Home />}
    </div>
)};

export default SignIn;
