import React, { useState } from 'react';
import { auth } from "../../Components/firebase/firebase"; // Assuming you have a firebase config file
import { useNavigate } from "react-router-dom";

function Header({user}) {
  const navigate = useNavigate();

    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const logout = () => {
        auth.signOut().then(() => {
          localStorage.clear();
          navigate("/");
        });
      };

    return(
        <header className="header">
            <a href="/home" className="logo">Pet Center</a>
            <i className="bx bx-menu" id="menu-icon"></i>
            <nav className="navbar">
                <a href="#home" className="active">Home</a>
                <a href="#about">About</a>
                <a href="#services">Services</a>
                <a href="#contact">Contact</a>
                {user ? (
                    <div className="dropdown">
                        <span onClick={toggleDropdown} className="username">{user.displayName || user.email}</span>
                        <div className={`dropdown-content ${dropdownOpen ? 'show' : ''}`}>
                            <a style={{display: "block"}} href="/update-account">Update Account</a>
                            <a style={{display: "block"}} href="/logout">Logout</a>
                        </div>
                    </div>
                ) : (
                    <button onClick={logout}> Login</button>
                )}
            </nav>
        </header>
    );
}

export default Header;
