import React, { useState, useEffect } from "react";
import { auth, provider } from "../firebase/firebase"; // Assuming config file with Firebase settings
import { doCreateUserWithEmailAndPassword } from "../firebase/auth";
import { signInWithPopup, signInWithEmailAndPassword} from "firebase/auth";
import Home from "./Home";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userEmail, setUserEmail] = useState(null); // Track logged-in user email
  const [error, setError] = useState(null); // Store error messages
  const [confirmPassword, setconfirmPassword] = useState('')
  const [isRegistering, setIsRegistering] = useState(false)

  const onSubmit = async (e) => {
    e.preventDefault()
    if(!isRegistering) {
        setIsRegistering(true)
        await doCreateUserWithEmailAndPassword(email, password)
    }
}

  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === "email") {
      setEmail(value);
    } else if (name === "password") {
      setPassword(value);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const data = await signInWithPopup(auth, provider); // Replace 'provider' with your Google Sign-in provider
      const userEmail = data.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
    } catch (error) {
      setError(error.message);
    }
  };

  const handleEmailLogin = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    setError(null); // Clear any previous errors

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCredential.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
    } catch (error) {
      setError(error.message);
    }
  };

  const handleClickButtonReg = async (event) => {
    const container = document.getElementById('container');
    container.classList.add("active");
  }
  const handleClickButtonLog = async (event) => {
    const container = document.getElementById('container');
    container.classList.remove("active");
  }

  useEffect(() => {
    const storedEmail = localStorage.getItem("email");
    setUserEmail(storedEmail);
  }, []); // Empty dependency array ensures it runs only once

  return (
    <div>
      {error && <p className="error">{error}</p>} {/* Display error message if any */}
      {!userEmail && ( // Only show login options if not logged in
        <>

          <div className="container" id="container">
        <div className="form-container sign-up">
            <form onSubmit={onSubmit}>
                <h1>Create Account</h1>
                <div className="social-icons">
                <button onClick={handleGoogleLogin}>Login with Google</button>
                </div>
                <span>or use your email for registeration</span>
                <input
                                type="email"
                                autoComplete='email'
                                required
                                value={email} onChange={(e) => { setEmail(e.target.value) }}
                                className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:indigo-600 shadow-sm rounded-lg transition duration-300"
                            />
                                            <input
                                disabled={isRegistering}
                                type="password"
                                autoComplete='new-password'
                                required
                                value={password} onChange={(e) => { setPassword(e.target.value) }}
                                className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg transition duration-300"
                            />
                            <div>

                            <label className="text-sm text-gray-600 font-bold">
                                Confirm Password
                            </label>
                            <input
                                disabled={isRegistering}
                                type="password"
                                autoComplete='off'
                                required
                                value={confirmPassword} onChange={(e) => { setconfirmPassword(e.target.value) }}
                                className="w-full mt-2 px-3 py-2 text-gray-500 bg-transparent outline-none border focus:border-indigo-600 shadow-sm rounded-lg transition duration-300"
                                />
                                </div>
                                <button
                            type="submit"
                            disabled={isRegistering}
                            className={`w-full px-4 py-2 text-white font-medium rounded-lg ${isRegistering ? 'bg-gray-300 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700 hover:shadow-xl transition duration-300'}`}
                        >
                            {isRegistering ? 'Signing Up...' : 'Sign Up'}
                        </button>
            </form>
        </div>
        <div class="form-container sign-in">
            <form onSubmit={handleEmailLogin}>
                <h1>Sign In</h1>
                <div className="social-icons">
                <button onClick={handleGoogleLogin}>Login with Google</button>
                </div>
                <span>or use your email password</span>
                <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={handleChange}
              required
            />
                            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={handleChange}
              required
            />
                <a href="#">Forget Your Password?</a>
                <button>Sign In</button>
            </form>
        </div>
        <div className="toggle-container">
            <div className="toggle">
                <div className="toggle-panel toggle-left">
                    <h1>Welcome Back!</h1>
                    <p>Enter your personal details to use all of site features</p>
                    <button className="hidden" id="login" onClick={handleClickButtonLog}>Sign In</button>
                </div>
                <div className="toggle-panel toggle-right">
                    <h1>Hello, Friend!</h1>
                    <p>Register with your personal details to use all of site features</p>
                    <button className="hidden" id="register" onClick={handleClickButtonReg}>Sign Up</button>
                </div>
            </div>
        </div>
    </div>
        </>
      )}
      {userEmail && <Home />} {/* Render Home if user is logged in */}
    </div>
  );
}

export default SignIn;
