import React, { useState, useEffect } from "react";
import { auth, provider } from "./config"; // Assuming config file with Firebase settings
import { signInWithPopup, signInWithEmailAndPassword } from "firebase/auth";
import Home from "./Home";

function SignIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userEmail, setUserEmail] = useState(null); // Track logged-in user email
  const [error, setError] = useState(null); // Store error messages

  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === "email") {
      setEmail(value);
    } else if (name === "password") {
      setPassword(value);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const data = await signInWithPopup(auth, provider); // Replace 'provider' with your Google Sign-in provider
      const userEmail = data.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
    } catch (error) {
      setError(error.message);
    }
  };

  const handleEmailLogin = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    setError(null); // Clear any previous errors

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const userEmail = userCredential.user.email;
      setUserEmail(userEmail);
      localStorage.setItem("email", userEmail); // Consider secure storage in production
    } catch (error) {
      setError(error.message);
    }
  };

  const handleClickButtonReg = async (event) => {
    const container = document.getElementById('container');
    container.classList.add("active");
  }
  const handleClickButtonLog = async (event) => {
    const container = document.getElementById('container');
    container.classList.remove("active");
  }

  useEffect(() => {
    const storedEmail = localStorage.getItem("email");
    setUserEmail(storedEmail);
  }, []); // Empty dependency array ensures it runs only once

  return (
    <div>
      {error && <p className="error">{error}</p>} {/* Display error message if any */}
      {!userEmail && ( // Only show login options if not logged in
        <>

          <div class="container" id="container">
        <div class="form-container sign-up">
            <form onSubmit={handleEmailLogin}>
                <h1>Create Account</h1>
                <div class="social-icons">
                <button onClick={handleGoogleLogin}>Login with Google</button>
                </div>
                <span>or use your email for registeration</span>
                <input type="text" placeholder="Name" />
                <input type="email" placeholder="Email" />
                <input type="password" placeholder="Password" />
                <button>Sign Up</button>
            </form>
        </div>
        <div class="form-container sign-in">
            <form onSubmit={handleEmailLogin}>
                <h1>Sign In</h1>
                <div class="social-icons">
                <button onClick={handleGoogleLogin}>Login with Google</button>
                </div>
                <span>or use your email password</span>
                <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={handleChange}
              required
            />
                            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={handleChange}
              required
            />
                <a href="#">Forget Your Password?</a>
                <button>Sign In</button>
            </form>
        </div>
        <div class="toggle-container">
            <div class="toggle">
                <div class="toggle-panel toggle-left">
                    <h1>Welcome Back!</h1>
                    <p>Enter your personal details to use all of site features</p>
                    <button class="hidden" id="login" onClick={handleClickButtonLog}>Sign In</button>
                </div>
                <div class="toggle-panel toggle-right">
                    <h1>Hello, Friend!</h1>
                    <p>Register with your personal details to use all of site features</p>
                    <button class="hidden" id="register" onClick={handleClickButtonReg}>Sign Up</button>
                </div>
            </div>
        </div>
    </div>
        </>
      )}
      {userEmail && <Home />} {/* Render Home if user is logged in */}
    </div>
  );
}

export default SignIn;
